import { useState } from 'react';
import SelectGenre from './select_genre';
import SelectYear from './select_year';
import SelectRuntime from './select_runtime';
import SelectAge from './select_age';



function SelectionSequence(){

    const [current, setCurrent] = useState('genre');

    const [genre, setGenre] = useState([]);
    const [year, setYear] = useState([]);
    const [runtime, setRuntime] = useState([]);
    const [age, setAge] = useState([]); 

    return (
        <div>
            { (current === 'genre') && <SelectGenre element={genre} set_element={setGenre}/>}
            { (current === 'year') && <SelectYear element={year} set_element={setYear}/>}     
            { (current === 'runtime') && <SelectRuntime element={runtime} set_element={setRuntime}/>}          

            { (genre.length != 0) && (current ==='genre') && <button className='next-button' onClick={()=>{setCurrent('year')}}>Next</button>}
            { (year.length != 0) && (current ==='year') && <button className='next-button' onClick={()=>{setCurrent('runtime')}}>Next</button>}

            <p className='test'>{ genre }</p>
            <p className='test'>{ year }</p>
            <p className='test'>{ runtime }</p>


        </div>
        
    );

}

export default SelectionSequence;
