function PickGenre({element, setElement}){

    function handleClick(genre){
        
        const genreLimit = 3;
        const numOfGenres = 0;
        const genreToggledOn = [false, false, false, false, false, false, false, false, false, false, false, false];
    

        switch(genre){
            case 0:
                if(genreToggledOn[genre]){
                    
                    numOfGenres--;
                    genreToggledOn[genre] = false;
                } else {
                    if(numOfGenres <= genreLimit){
                        setElement([element, 'action']);
                        genreToggledOn[genre] = true;
                    }
                }
        }
    }

    return (
        <div>
            <h2 className='home-page-picker-title'>1: Pick a genre</h2>
            <div className='genre-button-container'>
                <label>
                    <input class='genre-button' type='checkbox'/>
                </label>
            </div>
        </div>
    );
}

export default PickGenre;
