import { useState } from 'react';

function SelectRuntime({element, setElement}){

    const runtime_array = [
        {
            runtime: 'short',
            state: false
        }, {
            runtime: 'reasonable',
            state: false
        }, {
            runtime: 'over_reasonable',
            state: false
        }, {
            runtime: 'epic',
            state: false
        }, {
            runtime: 'very_epic',
            state: false
        },
    ];

    const desc = [
        {
            title: 'Short',
            desc: 'Under 90 Minutes'
        }, {
            title: 'Reasonable',
            desc: '90 to 120 Minutes'
        }, {
            title: 'Slightly Over Reasonable',
            desc: '120 to 150 Minutes'
        }, {
            title: 'Epic',
            desc: '150 to 180 Minutes'
        }, {
            title: 'Very Epic',
            desc: 'Over 180 Minutes'
        }
    ];

    const [runtimes, set_runtimes] = useState(runtime_array);

    return (
        <div>
            <h2 className='home-page-picker-title'>3: Pick a runtime range</h2>
            <div className='runtime-button-container'>
                {runtimes.map((element, index) => (
                    <label className={`runtime-button-${element.state ? 'on' : 'off'}`}>
                        <input className='runtime-button-checkbox' type='checkbox' defaultChecked={element.state} onClick={() => {handleClick(element.runtime)}}/>
                        <p className='runtime-button-text'>Prehistoric!<br/>1920 to 1940</p>
                    </label>
                ))}
            </div>
        </div>
    );

}

export default SelectRuntime;
