import { useState } from 'react';

function SelectRuntime({element, set_element}){

    const runtime_limit = 2;
    const [runtime_num, set_runtime_num] = useState(0);
    const [runtimes, set_runtimes] = useState([
        {
            runtime: 'short',
            state: false
        }, {
            runtime: 'reasonable',
            state: false
        }, {
            runtime: 'long',
            state: false
        }, {
            runtime: 'epic',
            state: false
        }, {
            runtime: 'eternity',
            state: false
        },
    ]);

    const descriptions = [
        {
            title: 'Short',
            desc: 'Under 90 Minutes'
        }, {
            title: 'Reasonable',
            desc: '90 to 120 Minutes'
        }, {
            title: 'Long',
            desc: '120 to 150 Minutes'
        }, {
            title: 'Epic',
            desc: '150 to 180 Minutes'
        }, {
            title: 'Eternity',
            desc: 'Over 180 Minutes'
        }
    ];


    function handle_click(runtime){


        /* 
        function removeGenre(x){
            setElement(element.filter((genre)=>{return genre != x}))
            setNumGenres(numGenres - 1);
        }

        function addGenre(x){
            setElement(element => [...element, x]);
            setNumGenres(numGenres + 1);
        }
        */

        const new_runtime_array = runtimes.map((x) => {
            if(x.runtime === runtime){
                if(x.state){
                    return {
                        ...x, 
                        state: false
                    }
                } else {
                    if(runtime_num < runtime_limit){
                        return {
                            ...x, 
                            state: true
                        }
                    }
                }
            
                /*
                set_element(runtime);
                return {
                    ...x, 
                    state: !x.state
                }
                */
            } else {
                return x;
            }
        })
        set_runtimes(new_runtime_array);
        
    }

    return (
        <div>
            <h2 className='home-page-picker-title'>3: Pick a runtime range</h2>
            <div className='runtime-button-container'>
                {runtimes.map((element, index) => (
                    <label key={index} className={`runtime-button-${element.state ? 'on' : 'off'}`}>
                        <input className='runtime-button-checkbox' type='checkbox' defaultChecked={element.state} onClick={() => {handle_click(element.runtime)}}/>
                        <p className='runtime-button-text'>{descriptions[index].title}<br/>{descriptions[index].desc}</p>
                    </label>
                ))}
            </div>
        </div>
    );

}

export default SelectRuntime;
