import { useState } from 'react';

function SelectRuntime({element, set_element}){

    const runtime_array = [
        {
            runtime: 'short',
            state: false
        }, {
            runtime: 'reasonable',
            state: false
        }, {
            runtime: 'over_reasonable',
            state: false
        }, {
            runtime: 'epic',
            state: false
        }, {
            runtime: 'very_epic',
            state: false
        },
    ];

    const description = [
        {
            title: 'Short',
            desc: 'Under 90 Minutes'
        }, {
            title: 'Reasonable',
            desc: '90 to 120 Minutes'
        }, {
            title: 'Slightly Over Reasonable',
            desc: '120 to 150 Minutes'
        }, {
            title: 'Epic',
            desc: '150 to 180 Minutes'
        }, {
            title: 'Very Epic',
            desc: 'Over 180 Minutes'
        }
    ];

    const runtime_limit = 2;
    const [runtime_num, set_runtime_num] = useState(0);
    const [runtimes, set_runtimes] = useState(runtime_array);

    function handle_click(runtime){

        function add_runtime(x){
            set_element(element => [...element, x]);
            set_runtime_num(runtime_num + 1);
        }

        function remove_runtime(x){
            set_element(element.filter((runtime)=>{return runtime != x}))
            set_runtime_num(runtime_num - 1);
        }

        let index = -1;

        runtimes.map((x) => {
            if(runtime == x.runtime)
                x.state = true;
        }) 

        for(let i = 0; i < 5; i++){
            if(runtimes[i].runtime == runtime){
                index = i;
            }
        }
        if (index != -1){

        }
    }

    return (
        <div>
            <h2 className='home-page-picker-title'>3: Pick a runtime range</h2>
            <div className='runtime-button-container'>
                {runtimes.map((element, index) => (
                    <label key={element} className={`runtime-button-${element.state ? 'on' : 'off'}`}>
                        <input className='runtime-button-checkbox' type='checkbox' defaultChecked={element.state} onClick={() => {handle_click(element.runtime)}}/>
                        <p className='runtime-button-text'>{description[index].title}<br/>{description[index].desc}</p>
                    </label>
                ))}
            </div>
        </div>
    );

}

export default SelectRuntime;
