import { useState } from "react";

function MyList() {
  const movies = [
    {
      title: "John Wick: Chapter 4",
      genres: ["Action", "Thriller", "Crime"],
      year: 2023,
      runtime: "170 Minutes",
      rating: "R",
    },
    {
      title: "The Super Mario Bros. Movie",
      genres: ["Animation", "Adventure", "Comedy"],
      year: 2023,
      runtime: "92 Minutes",
      rating: "PG",
    },
  ];

  const [liked, set_liked] = useState(false);
  const [disliked, set_disliked] = useState(false);

  const handle_click = (rating) => {
    if (rating === "like") {
      set_liked(!liked);
      set_disliked(false);
    } else if (rating === "disliked") {
      set_liked(false);
      set_disliked(!dislike);
    }
  };

  return (
    <div>
      <h1 className="heading">My List</h1>

      <div className="list-container">
        {movies.map((movie) => (
          <div key={movie.title} className="movie-container">
            <div className="movie-info">
              <p className="movie-title">{movie.title}</p>
              <p className="movie-desc">
                {movie.genres} | {movie.year} | {movie.runtime} | {movie.rating}
              </p>
            </div>
            <div className="movie-rating-container">
              <label className={`like-${liked ? "on" : "off"}`}>
                <input
                  className="movie-rating-checkbox"
                  type="checkbox"
                  onClick={() => {
                    handle_click("like");
                  }}
                />
                <span class="material-symbols-outlined">thumb_up</span>
                <p>Like</p>
              </label>
              <label className={`dislike-${disliked ? "on" : "off"}`}>
                <input
                  className="movie-rating-checkbox"
                  type="checkbox"
                  onClick={() => {
                    handle_click("dislike");
                  }}
                />
                <span class="material-symbols-outlined">thumb_down</span>
                <p>Dislike</p>
              </label>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MyList;
