import { useState } from 'react';

function SelectYear({element, setElement}){

    const year_limit = 2;
    const [year_num, set_year_num] = useState(0);
    const [years, set_years] = useState([
        {
            year: 'prehistoric',
            state: false
        }, {
            year: 'vintage',
            state: false
        }, {
            year: 'classic',
            state: false
        }, {
            year: 'retro',
            state: false
        }, {
            year: 'recent',
            state: false
        },
    ]);

    const descriptions = [
        {
            title: 'Prehistoric',
            desc: '1920 to 1940'
        }, {
            title: 'Vintage',
            desc: '1940 to 1960'
        }, {
            title: 'Classic',
            desc: '1960 to 1980'
        }, {
            title: 'Retro',
            desc: '1980 to 2000'
        }, {
            title: 'Recent',
            desc: '2000 to 2020'
        },
    ]

