    }

    return (
        <div>
            <h2 className='home-page-picker-title'>2: Pick a year range</h2>
            <div className='year-button-container'>
                {years.map((element, index) => (
                    <label className={`year-button-${element.state ? 'on' : 'off'}`}>
                        <input className='year-button-checkbox' type='checkbox' defaultChecked={element.state} onClick={() => {handle_click(element.year)}}/>
                        <p className='year-button-text'>{descriptions[index].title}!<br/>{descriptions[index].desc}</p>
                    </label>
                ))}
                
                
            </div>

        </div>
    );
}

export default SelectYear;
