import { useState } from 'react';

function PickGenre({element, setElement}){

    const [action, toggleAction] = useState(false);
    const [comedy, toggleComedy] = useState(false);
    const [drama, toggleDrama] = useState(false);
    const [horror, toggleHorror] = useState(false);
    const [romance, ToggleRomance] = useState(false);
    const [thriller, toggleThriller] = useState(false);
    const [mystery, toggleMystery] = useState(false);
    const [crime, toggleCrime] = useState(false);
    const [animation, toggleAnimation] = useState(false);
    const [scifi, toggleScifi] = useState(false);
    const [fantasy, toggleFantasy] = useState(false);
    const [adventure, toggleAdventure] = useState(false);

    function handleClick(genre){
        
        const genreLimit = 3;
        const numOfGenres = 0;
    
        switch(genre){
            case 'action':
                if(action){
                    //filter genre out
                    numOfGenres--;
                    toggleAction(!action)
                } else {
                    if(numOfGenres <= genreLimit){
                        setElement([element, 'action']);
                        toggleAction(!action)
                    }
                }
        }
    }

    return (
        <div>
            <h2 className='home-page-picker-title'>1: Pick a genre</h2>
            <div className='genre-button-container'>

                <label>
                    <input type='checkbox' defaultChecked={action} onClick={() => {handleClick('action')}}/>
                    <span/>
                    <p>Action</p>
                </label>

                <button className='genre-button'onClick={() => {setElement([element, 'action'])}}>Action</button>
                <button className='genre-button'onClick={() => {setElement([element, 'comedy'])}}>Comedy</button>
                <button className='genre-button'onClick={() => {setElement([element, 'drama'])}}>Drama</button>
                <button className='genre-button'onClick={() => {setElement([element, 'horror'])}}>Horror</button>
                <button className='genre-button'onClick={() => {setElement([element, 'romance'])}}>Romance</button>
                <button className='genre-button'onClick={() => {setElement([element, 'thriller'])}}>Thriller</button>
                <button className='genre-button'onClick={() => {setElement([element, 'mystery'])}}>Mystery</button>
                <button className='genre-button'onClick={() => {setElement([element, 'crime'])}}>Crime</button>
                <button className='genre-button'onClick={() => {setElement([element, 'animation'])}}>Animation</button>
                <button className='genre-button'onClick={() => {setElement([element, 'scifi'])}}>Sci Fi</button>
                <button className='genre-button'onClick={() => {setElement([element, 'fantasy'])}}>Fantasy</button>
                <button className='genre-button'onClick={() => {setElement([element, 'adventure'])}}>Adventure</button>
            </div>
        </div>
    );
}

export default PickGenre;
